import Header from "./Header";

function App() {
  const Features = [
    {
      id: 1,
      title: "Log Calls",
    },
    {
      id: 2,
      title: "Task",
    },
    {
      id: 3,
      title: "Event",
    },
    {
      id: 4,
      title: "Make Note",
    },
    {
      id: 5,
      title: "Add Attachment",
    },
    {
      id: 6,
      title: "Activity Set",
    },
    {
      id: 7,
      title: "Custom Field",
    },
  ];

  return (
    <div className="bg-gray-200 h-full py-16">
      <div className="w-9/12 mx-auto">
        <Header />
        <div className="relative">
          <div className="flex gap-x-6 text-gray-700 text-xl uppercase">
            <span className="cursor-pointer text-orange-500 border-4 border-b-orange-500 absolute bottom-0">
              Activity
            </span>
            <span className="cursor-pointer ml-32">Information</span>
          </div>
          <hr className="bg-gray-500 text-black h-1 w-full " />
        </div>

        {/* --------------------left-------------------- */}
        <div className="w-7/12 mt-6 border border-gray-400 pt-6 bg-white h-auto">
          <div className="w-full border-3 border-b border-gray-500 pb-4">
            <ul className="flex justify-center gap-x-8 ">
              {Features.map((item, i) => {
                return (
                  <li
                    className={`${
                      item.id === 2 &&
                      "bg-yellow-200 border-2 border-b-yellow-400 py-1 px-4"
                    }  cursor-pointer `}
                    key={i}
                  >
                    {item.title}
                  </li>
                );
              })}
            </ul>
          </div>
          <div className="p-12">
            <span className="font-semibold text-lg">New Task</span>
            <div className="flex pt-4 w-full justify-between gap-x-8">
              <label htmlFor="" className="flex flex-col w-6/12">
                <div className="">
                  Task type <span className="text-red-500">*</span>
                </div>

                <div>
                  <button
                    type="button"
                    class="inline-flex justify-center w-full rounded-md border border-gray-300 shadow-sm px-4 py-2 bg-white text-sm font-medium text-gray-700 hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-offset-gray-100 focus:ring-indigo-500"
                    id="menu-button"
                    aria-expanded="true"
                    aria-haspopup="true"
                  >
                    <svg
                      class="-mr-1 ml-auto h-5 w-5"
                      xmlns="http://www.w3.org/2000/svg"
                      viewBox="0 0 20 20"
                      fill="currentColor"
                      aria-hidden="true"
                    >
                      <path
                        fill-rule="evenodd"
                        d="M5.293 7.293a1 1 0 011.414 0L10 10.586l3.293-3.293a1 1 0 111.414 1.414l-4 4a1 1 0 01-1.414 0l-4-4a1 1 0 010-1.414z"
                        clip-rule="evenodd"
                      />
                    </svg>
                  </button>
                </div>
              </label>
              <label htmlFor="" className="flex flex-col w-6/12">
                <div className="">Subject</div>
                <input type="text" className="border border-gray-300 h-10" />
              </label>
            </div>
            <div className="flex">
              <div className="">
                <input type="text" className="border border-gray-300 h-10" />
              </div>
              <div className="">
                <textarea name="" id="" cols="30" rows="10" className="border-gray-300"></textarea>
              </div>
            </div>
          </div>
        </div>
        {/* --------------------left-------------------- */}

        {/* ------------------------right------------------------ */}
        <div className="w-4/12"></div>
        {/* ------------------------right------------------------ */}
      </div>
    </div>
  );
}

export default App;
